css:
.header {
          background: linear-gradient(135deg, #eb0dff 0%, #9b0ffe 100%);
          color: white;
          padding: 25px;
          text-align: center;
      }
      
      .event-title {
          font-size: 32px;
          font-weight: 700;
          margin: 0 0 10px 0;
          font-size:12px;
      }
      
      .event-type {
          display: inline-block;
          background: rgba(255, 255, 255, 0.2);
          padding: 6px 18px;
          border-radius: 20px;
          font-size: 16px;
          margin-top: 8px;
          backdrop-filter: blur(5px);
      }
      
      .content {
          padding: 30px;
      }
      
      .master-table {
          width: 100%;
          border-radius: 8px;
      }
      
      .master-table th {
          color:Black;
          text-align: left;
          padding: 16px;
          font-size:12px;
          font-weight: 500;
          border: 0.5px solid #89CFF0;
          background-color: #dce4e6;
      }
      
      .master-table td {
          padding: 16px;
          border: 0.5px solid #89CFF0;
          font-size:12px;
      }
  
      
      .no-data {
          text-align: center;
          color: #888;
          font-style: italic;
          padding: 20px;
          background: #f9f9f9;
          border-radius: 6px;
      }
